// apps/generate-address.js
// generate a simple ECDSA keypair (secp256k1) and show address (RIPEMD160(SHA256(pubkey)))
// usage: node apps/generate-address.js

const EC = require('elliptic').ec;
const ec = new EC('secp256k1');
const crypto = require('crypto');

const key = ec.genKeyPair();
const pub = key.getPublic(true, 'hex'); // compressed
const priv = key.getPrivate('hex');

const ripemd160 = crypto.createHash('ripemd160').update(Buffer.from(crypto.createHash('sha256').update(Buffer.from(pub,'hex')).digest())).digest('hex');

console.log('privateKey:', priv);
console.log('pubKey:', pub);
console.log('address:', ripemd160);
