commits.content:
body {
  font-family: 'Segoe UI', sans-serif;
  background: #0d1117;
  color: #e6edf3;
  margin: 0;
  padding: 0;
}

header {
  background: #161b22;
  padding: 12px 0;
  text-align: center;
  border-bottom: 1px solid #30363d;
}

h1 {
  color: #58a6ff;
  margin: 0;
}

.container {
  max-width: 900px;
  margin: 30px auto;
  background: #161b22;
  padding: 20px;
  border-radius: 8px;
  box-shadow: 0 0 8px #0003;
}

input {
  width: 70%;
  padding: 10px;
  border: 1px solid #30363d;
  border-radius: 6px;
  background: #0d1117;
  color: #e6edf3;
  font-size: 14px;
}

button {
  padding: 10px 16px;
  margin-left: 10px;
  background: #238636;
  border: none;
  color: #fff;
  border-radius: 6px;
  cursor: pointer;
  font-size: 14px;
}
button:hover {
  background: #2ea043;
}

.balance-card {
  margin-top: 20px;
  padding: 12px;
  border-radius: 6px;
  background: #21262d;
  display: flex;
  justify-content: space-between;
  font-weight: bold;
  color: #58a6ff;
}

.tx-table {
  width: 100%;
  border-collapse: collapse;
  margin-top: 20px;
}
.tx-table th {
  background: #21262d;
  color: #8b949e;
  padding: 8px;
}
.tx-table td {
  border-bottom: 1px solid #30363d;
  padding: 8px;
  text-align: center;
}
.tx-table tr:hover {
  background: #1f6feb22;
}

.incoming {
  color: #3fb950;
}
.outgoing {
  color: #f85149;
}
.mempool {
  color: #d29922;
}

@media (max-width: 600px) {
  input { width: 60%; }
  .container { padding: 10px; }
}
